const MiddleContent = () => {
  const Search = () => {
    const [searchValue, setSearchValue] = useState("");
    const handleInputChange = (e) => {
      setSearchValue(e.target.value);
    };

    const handleSuggestClick = (text) => {
      setSearchValue(text);
    };

    return (
      <div className="search-container" id="Search">
        <div className="search-component">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
          >
            <path
              d="M11 19C15.4183 19 19 15.4183 19 11C19 6.58172 15.4183 3 11 3C6.58172 3 3 6.58172 3 11C3 15.4183 6.58172 19 11 19Z"
              stroke="#71717A"
              stroke-width="2"
              stroke-linecap="round"
              stroke-linejoin="round"
            />
            <path
              d="M21 21L16.65 16.65"
              stroke="#71717A"
              stroke-width="2"
              stroke-linecap="round"
              stroke-linejoin="round"
            />
          </svg>
          <input
            type="text"
            placeholder="Search our Documentation and courses"
            value={searchValue}
            onChange={handleInputChange}
          />
        </div>
        <div className="search-suggest">
          {dummySuggest.length > 0 &&
            dummySuggest.map((item, index) => (
              <div
                className="search-suggest__item"
                key={index}
                onClick={() => handleSuggestClick(item.text)}
                style={{ cursor: "pointer" }}
              >
                {item.text}
              </div>
            ))}
        </div>
      </div>
    );
  };
  const Find = () => {
    const ContentItem = ({ item }) => {
      return (
        <div className={`content-item ${item.className}`}>
          <div className="content-item__icon">{item.icon}</div>
          <p className="content-item__title">{item.title}</p>
          <p className="content-item__des">{item.des}</p>
          <a className="content-item__link" href={item.link}>
            EXPLORE MORE{" "}
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              viewBox="0 0 16 16"
              fill="none"
            >
              <path
                d="M6 12L10 8L6 4"
                stroke="#FAFAFA"
                stroke-linecap="round"
                stroke-linejoin="round"
              />
            </svg>
          </a>
        </div>
      );
    };
    return (
      <div className="find-container" id="Find">
        <h5 className="title">What can I find here</h5>
        <p className="des">
          Our documentation has three main sections, the first dedicated to
          Demo, the second dedicated to free tools such as Proxy Manager, and
          the third dedicated to our integrations.
        </p>
        <div className="content">
          {listFind.length > 0 &&
            listFind.map((item, index) => (
              <ContentItem item={item} key={index} />
            ))}
        </div>
      </div>
    );
  };
  const Documentation = () => {
    return (
      <div className="documentation-container" id="Documentation">
        <h5 className="title">Demo Documentation</h5>
        <p className="des">
          Demo is a high-quality Residential Proxy platform designed to help you
          maintain stable and secure connections at the lowest cost on the
          market. We offer modern features such as auto-refresh proxies, IP
          rotation, flexible configured ports, and usage code sharing. Get
          started today with our affordable and efficient service plans.{" "}
          <a href="">Start now.</a>
        </p>
        <p className="sub-des">
          If you can't find something or need assistance, please contact us
          at support@Demo.com or via our 24/7 live chat.
        </p>
      </div>
    );
  };
  const Explore = () => {
    const ExploreItem = ({ item }) => {
      return (
        <a href={item.link} className="explore-list__item">
          <div className="explore-list__item--icon">{item.icon}</div>
          <div className="explore-list__item--info">
            <p className="title">{item.title}</p>
            <p className="count">{item.count} articles</p>
          </div>
        </a>
      );
    };
    return (
      <div className="explore-container" id="Explore">
        <h5 className="title">Explore the docs</h5>
        <div className="explore-list">
          {dummyExploreList.length > 0 &&
            dummyExploreList.map((item, index) => (
              <ExploreItem key={index} item={item} />
            ))}
        </div>
      </div>
    );
  };
  return (
    <MiddleContentContainer>
      <div className="container">
        <Search />
        <Find />
        <Documentation />
        <Explore />
      </div>
    </MiddleContentContainer>
  );
};
export default MiddleContent;
